// src/components/Login.js
import React from 'react';
import GoogleLoginButton from './googleLoginButton';

function Login() {
  const handleSuccess = (response) => {
    // Handle the Google login response here
    // You can send the response to your server for further processing
    console.log('Login successful:', response);
    alert('Login successful');
  };

  const handleFailure = (error) => {
    // Handle any errors that occur during Google login
    console.error('Login failed:', error);
    alert('Login failed');
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md p-8 bg-white rounded-lg shadow-lg">
        <h2 className="text-2xl font-bold text-center mb-6">Login</h2>
        <div className="flex justify-center mb-6">
          <GoogleLoginButton onSuccess={handleSuccess} onFailure={handleFailure} />
        </div>
      </div>
    </div>
  );
}

export default Login;
