// src/api.js
import axios from 'axios';

const API_URL = 'http://localhost:5000/api'; // Adjust this if your backend is hosted elsewhere

export const login = (googleId, name, email) => {
  return axios.post(`${API_URL}/login`, { googleId, name, email });
};

export const createQuiz = (title, creatorId, questions) => {
  return axios.post(`${API_URL}/quizzes`, { title, creatorId, questions });
};

export const getQuiz = (quizId) => {
  return axios.get(`${API_URL}/quizzes/${quizId}`);
};

export const updateQuiz = (quizId, title, questions) => {
  return axios.put(`${API_URL}/quizzes/${quizId}`, { title, questions });
};

export const getQuizList = () => {
  return axios.get(`${API_URL}/quizzes`); // Ensure this endpoint exists in your backend
};
