import React, { useEffect, useState } from 'react';
import { getQuizList } from '../api';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import PropTypes from 'prop-types'; // Import PropTypes for prop validation

function QuizList({ user }) {
  const [quizzes, setQuizzes] = useState([]);
  const navigate = useNavigate(); // Hook for navigation

  useEffect(() => {
    // Check if user is defined and has a username
    if (user && user.username) {
      const fetchQuizzes = async () => {
        try {
          const response = await getQuizList(user.username);
          setQuizzes(response.data);
        } catch (error) {
          console.error('Error retrieving quizzes:', error);
        }
      };

      fetchQuizzes();
    } else {
      console.error('User is not defined or missing username');
    }
  }, [user]);

  const handleQuizClick = (title) => {
    navigate(`/join-quiz?title=${encodeURIComponent(title)}`); // Navigate with the quiz title
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-6">Your Quizzes</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {quizzes.map((quiz) => (
          <div
            key={quiz._id}
            className="bg-white p-6 rounded-lg shadow-lg cursor-pointer hover:bg-gray-100 transition"
            onClick={() => handleQuizClick(quiz.title)}
          >
            <h3 className="text-xl font-bold mb-2">{quiz.title}</h3>
            <p className="text-gray-600 text-sm">Created by: {quiz.creatorId}</p>
            <p className="text-gray-500 text-sm">Questions: {quiz.questions.length}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

// PropTypes validation
QuizList.propTypes = {
  user: PropTypes.shape({
    username: PropTypes.string,
  }),
};

// Default props
QuizList.defaultProps = {
  user: {},
};

export default QuizList;
