// src/components/JoinQuiz.js
import React, { useState } from 'react';
import { getQuiz } from '../api';

function JoinQuiz() {
  const [quizTitle, setQuizTitle] = useState('');
  const [quiz, setQuiz] = useState(null);
  const [userAnswers, setUserAnswers] = useState([]);
  const [score, setScore] = useState(null);
  const [quizNotFound, setQuizNotFound] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setQuizNotFound(false);
    setScore(null);
    try {
      const response = await getQuiz(quizTitle);
      if (response.data) {
        setQuiz(response.data);
        setUserAnswers(Array(response.data.questions.length).fill(''));
      } else {
        setQuiz(null);
        setQuizNotFound(true);
      }
    } catch (error) {
      console.error(error);
      alert('Error retrieving quiz');
    }
  };

  const handleAnswerChange = (index, value) => {
    const newUserAnswers = [...userAnswers];
    newUserAnswers[index] = value;
    setUserAnswers(newUserAnswers);
  };

  const handleQuizSubmit = (e) => {
    e.preventDefault();
    if (quiz) {
      let newScore = 0;
      quiz.questions.forEach((q, index) => {
        if (q.correctAnswer === userAnswers[index]) {
          newScore++;
        }
      });
      setScore(newScore);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
      <h2 className="text-2xl font-bold mb-4">Join Quiz</h2>
      <form onSubmit={handleSubmit} className="mb-6">
        <input
          type="text"
          placeholder="Enter Quiz Title"
          value={quizTitle}
          onChange={(e) => setQuizTitle(e.target.value)}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          required
        />
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline mt-4"
        >
          Join Quiz
        </button>
      </form>
      {quizNotFound && (
        <div className="text-red-500 font-bold mb-4">
          Quiz doesn't exist
        </div>
      )}
      {quiz && (
        <div className="w-full max-w-2xl bg-white p-6 rounded-lg shadow-lg">
          <h3 className="text-xl font-bold mb-4">{quiz.title}</h3>
          <form onSubmit={handleQuizSubmit}>
            {quiz.questions.map((q, index) => (
              <div key={index} className="mb-6">
                <strong>{q.question}</strong>
                <div>
                  {q.options.map((option, i) => (
                    <label key={i} className="block mt-2">
                      <input
                        type="radio"
                        name={`question-${index}`}
                        value={option}
                        checked={userAnswers[index] === option}
                        onChange={(e) => handleAnswerChange(index, e.target.value)}
                        className="mr-2"
                      />
                      {option}
                    </label>
                  ))}
                </div>
              </div>
            ))}
            <button
              type="submit"
              className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Submit Quiz
            </button>
          </form>
          {score !== null && (
            <div className="mt-4">
              <h3 className="text-2xl font-bold">
                Your score: {score} / {quiz.questions.length}
              </h3>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default JoinQuiz;
