// src/components/GoogleLoginButton.js
import React from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';

const GoogleLoginButton = ({ onSuccess, onFailure }) => {
  const clientId = '268897960072-80c10ak092da9alcg74d3ev83iu3qhm6.apps.googleusercontent.com'; // Replace with your Google Client ID

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <GoogleLogin
        onSuccess={onSuccess}
        onError={onFailure}
        logoAlignment="left" // Optional, aligns Google logo to the left
        text="Sign in with Google" // Optional, button text
      />
    </GoogleOAuthProvider>
  );
};

export default GoogleLoginButton;
