import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getQuizList } from '../api';

function EditQuiz({ user }) {
    const [quizzes, setQuizzes] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchQuizzes = async () => {
            if (user && user.username) {
                try {
                    const response = await getQuizList(user.username);
                    console.log('Fetched quizzes:', response.data);
                    const data = Array.isArray(response.data) ? response.data : [response.data];
                    setQuizzes(data);
                } catch (error) {
                    console.error('Error fetching quizzes:', error);
                    setQuizzes([]);
                }
            }
        };

        fetchQuizzes();
    }, [user]);

    const handleEditClick = (quizId) => {
        navigate(`/edit-quiz/${quizId}`);
    };

    return (
        <div className="p-4">
            <h2 className="text-2xl font-bold mb-6">Edit Your Quizzes</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {quizzes.map((quiz) => (
                    <div
                        key={quiz._id}
                        className="bg-white p-6 rounded-lg shadow-lg flex justify-between items-center"
                    >
                        <h3 className="font-bold mb-2">{quiz.title}</h3>
                        <button
                            onClick={() => handleEditClick(quiz._id)}
                            className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
                        >
                            Edit
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default EditQuiz;
