//googlelogin.js
import React from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';

const GoogleLoginComponent = ({ onSuccess, onFailure }) => {
  const clientId = process.env.REACT_APP_GOOGLE_CLIENT_ID;

  const handleSuccess = async (response) => {
    const token = response.credential; // This is the token you need

    try {
      const res = await fetch('http://localhost:5000/api/auth/google-login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ token }),
      });

      const data = await res.json();

      if (res.ok) {
        onSuccess(data);
      } else {
        onFailure(data);
      }
    } catch (error) {
      onFailure(error);
    }
  };

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <GoogleLogin
        onSuccess={handleSuccess}
        onError={(error) => onFailure(error)}
        logoAlignment="left" // Optional, aligns Google logo to the left
        text="Sign in with Google" // Optional, button text
      />
    </GoogleOAuthProvider>
  );
};

export default GoogleLoginComponent;
